mapper.select:
select * from (select *,ROW_NUMBER() OVER (ORDER BY toweraddtime desc) as rank from( select o.id as id, u.User_name as username, u.User_tele as usertele, a.area_name as towerareaname, c.City_name as towercityname, t.Tower_name as towername, o.Tower_state as towerstate, o.Tower_fee as towerfee, CONVERT(varchar, o.Tower_add_time, 120 ) as toweraddtime, case o.Tower_state when 0 then '抢单' when 1 then '弃单' when 2 then '交单' when 3 then '审核拒绝(签约)' when 4 then '审核成功(签约)' when 5 then '付首款审核中' when 6 then '付首款审核成功' when 7 then '付首款审核拒绝' when 8 then '付尾款审核中' when 9 then '完成' when 10 then '付尾款审核拒绝' when 11 then '超时过期' when 12 then '延期' when 13 then '延期申请中' when 14 then '延期拒绝' end as towerstatename from tower_order_detail o left join tower_user_info u on u.Wx_id=o.Tower_wx_id left join tower_tower_info t on t.ID=o.Tower_id left join tower_area a on a.ID=t.Tower_area left join tower_city c on c.City_id=a.City_id left join tower_area ua on ua.ID=u.User_area left join tower_city uc on uc.City_id=ua.City_id where 1=1 and t.Tower_is_hb=1
and a.City_id=#{admincity}
and a.ID in(
#{area.id}
)
and u.User_name like '%${parameter.username}%'
and u.User_tele=#{parameter.usertele}
and t.Tower_name like '%${parameter.towername}%'
and t.Tower_area=#{parameter.towerarea}
and o.Tower_state=#{parameter.towerstate}
and ua.City_id=#{parameter.usercity}
and ua.id=#{parameter.userarea}
and o.Tower_add_time >=#{parameter.toweraddtimestart}
and o.Tower_add_time <= #{parameter.toweraddtimeend}
and o.Tower_state in(5,8)
)w1)as tmp where tmp.rank between #{pageno}*10-9 and #{pageno}*10

		select
		count(1)
		from tower_order_detail o
		left join tower_user_info u on
		u.Wx_id=o.Tower_wx_id
		left join tower_tower_info t on t.ID=o.Tower_id
		left join tower_area a on a.ID=t.Tower_area
		left join tower_city c on
		c.City_id=a.City_id
		left join tower_area ua on ua.ID=u.User_area
		left
		join tower_city uc on uc.City_id=ua.City_id
		where 1=1
		and t.Tower_is_hb=1
		
		
			and a.City_id=#{admincity}
		
		
			and a.ID in(
			
				#{area.id}
			
			)
		
		
		
			and u.User_name like '%${parameter.username}%'
		
		
			and u.User_tele=#{parameter.usertele}
		
		
			and t.Tower_name like '%${parameter.towername}%'
		
		
			and t.Tower_area=#{parameter.towerarea}
		
		
			and o.Tower_state=#{parameter.towerstate}
		
		
			and ua.City_id=#{parameter.usercity}
		
		
			and ua.id=#{parameter.userarea}
		

		
			and o.Tower_add_time >=#{parameter.toweraddtimestart}
		
		
			and o.Tower_add_time  <= 
			#{parameter.toweraddtimeend}
		
		
		    and o.Tower_state in(5,8)

mapper.update:
update tower_tower_info
set Tower_state=#{towerstate}
where ID=#{towerid}
;

mapper.select:
 select count(1)
 from tower_user_order_info
 where Wx_id=#{wxid}
;

select o.Order_id as orderid, u.User_name as username, u.User_tele as usertele, case u.User_level when 1 then '一级用户' when 2 then '二级用户' when 3 then '三级用户' when 4 then '四级用户' end as userlevelname, ua.area_name as userareaname, uc.City_name as usercityname, u.User_company as usercompany, t.Tower_name as towername, a.area_name as towerareaname, c.City_name as towercityname, t.Tower_address as toweraddress, case t.Tower_level when 1 then '一级站址' when 2 then '二级站址' when 3 then '三级站址' when 4 then '四级站址' end as towerlevelname, p.Tower_type_name as towertypename, t.Tower_large as towerlarge, t.Tower_j as towerj, t.Tower_w as towerw, t.Tower_manager as towermanager, t.Tower_info as towerinfo, case o.Tower_state when 0 then '抢单' when 1 then '弃单' when 2 then '交单' when 3 then '审核拒绝(签约)' when 4 then '审核成功(签约)' when 5 then '付首款审核中' when 6 then '付首款审核成功' when 7 then '付首款审核拒绝' when 8 then '付尾款审核中' when 9 then '完成' when 10 then '付尾款审核拒绝' when 11 then '超时过期' when 12 then '延期' when 13 then '延期申请中' when 14 then '延期拒绝' end as towerstatename, o.Tower_fee as towerfee, o.Tower_first_fee as towerfirstfee, o.Tower_end_fee as towerendfee, CONVERT(varchar, o.Tower_add_time, 120 ) as toweraddtime
from tower_order_detail o left join tower_user_info u on u.Wx_id=o.Tower_wx_id left join tower_tower_info t on t.ID=o.Tower_id left join tower_area a on a.ID=t.Tower_area left join tower_city c on c.City_id=a.City_id left join tower_area ua on ua.ID=u.User_area left join tower_city uc on uc.City_id=ua.City_id left join tower_tower_type p on p.ID=t.Tower_type
where 1=1 and t.Tower_is_hb=0
and a.City_id=#{admincity}
and a.ID in(
#{area.id}
)
and u.User_name like '%${parameter.username}%'
and u.User_tele=#{parameter.usertele}
and t.Tower_name like '%${parameter.towername}%'
and t.Tower_area=#{parameter.towerarea}
and o.Tower_state=#{parameter.towerstate}
and ua.City_id=#{parameter.usercity}
and ua.id=#{parameter.userarea}
and o.Tower_add_time >=#{parameter.toweraddtimestart}
and o.Tower_add_time <= #{parameter.toweraddtimeend}
and o.Tower_state in(5,8)
order by o.Tower_add_time desc
