mapper.select:
select * from (select *,ROW_NUMBER() OVER (ORDER BY adddate desc) as rank from( select a.ID as id, a.Admin_id as adminid, a.Admin_name as adminname, a.Admin_power as adminpower, a.Admin_is_bind as adminisbind, p.Power_name as adminpowerName,
a.Admin_wxid as wxid, u.Wx_name as wxname, u.User_tele as wxphone, CONVERT(varchar, a.Admin_add_time, 120 ) as adddate
from tower_admin a left join tower_user_info u on u.Wx_id = a.Admin_wxid left join tower_power p on a.Admin_power = p.ID
where 1=1
and a.Admin_Area_city=#{adminCity}
and (
charindex(',${area.id},',',' + a.Admin_area + ',')>0
)
and a.Admin_id like '%${queryItem.adminId}%'
and a.Admin_name like '%${queryItem.adminName}%'
and a.Admin_Area_city = #{queryItem.cityId}
and charindex(',' + convert(varchar(10),#{queryItem.areaId}) + ',' , ',' + a.Admin_area + ',')>0
and a.Admin_power = #{queryItem.adminPower}
and a.Admin_phone = #{queryItem.adminPhone}
)w1)as tmp where tmp.rank between #{pageNo}*10-9 and #{pageNo}*10

select count(1) from tower_admin a left join tower_user_info u on u.Wx_id = a.Admin_wxid left join tower_power p on a.Admin_power = p.ID
where 1=1
and a.Admin_Area_city=#{adminCity}
and (
charindex(',${area.id},',',' + a.Admin_area + ',')>0
)
and a.Admin_id like '%${queryItem.adminId}%'
and a.Admin_name like '%${queryItem.adminName}%'
and a.Admin_Area_city = #{queryItem.cityId}
and charindex(',' + convert(varchar(10),#{queryItem.areaId}) + ',' , ',' + a.Admin_area + ',')>0
and a.Admin_power = #{queryItem.adminPower}
and a.Admin_phone = #{queryItem.adminPhone}

