mapper.select:
		select * from (select *,ROW_NUMBER() OVER (ORDER BY adddate desc) as rank from(
  		select 
			a.ID as id,
		    b.User_name as username,
		    b.User_tele as usertele,
		    c.area_name as areaname,
		    d.City_name as cityname,
		    (case b.user_level when 1 then '一级用户' when 2 then '二级用户' when 3 then '三级用户' when 4 then '四级用户' end ) as levelname,
		    g.Type_name as typename,
		    a.Fee as fee,
		    a.Add_date as adddate,
		    (case a.state when 0 then '提现申请中' when 1 then '审核通过，待打款' when 2 then '审核驳回' when 3 then '已打款' when 4 then '打款失败'  when 5 then '用户已确认' when 6 then '自动确认' end ) as statename,
		    a.state
		     from tower_user_tx_record a,
		     tower_user_info b left join tower_user_type g on b.User_type = g.ID,
		     tower_area c,
		     tower_city d
		where 1 = 1
		  
		 
		  and c.City_id=#{admincity}
		  
		  
	   and c.City_id in(
	  
	   #{city.cityid}
	  
	  )
	  
		  
		  and c.ID in(
		  
		   #{area.id}
		  
		  )
		  
		 
		  and a.Wx_id = b.Wx_id 
		  and b.User_area = c.ID
		  and d.City_id = c.City_id
		  
		  and b.User_tele=#{parameter.usertele}
		  
		  
		  and b.User_name=#{parameter.username}
		  
		  
		  and b.User_area=#{parameter.userarea}
		  
		   
		  and c.City_id=#{parameter.usercity}
		  
		  
		  and b.user_level=#{parameter.userlevel}
		  
		  
		  and b.User_type=#{parameter.usertype}
		  
		  
		  and a.state=#{parameter.state}
		  
		  ) w1)as t where t.rank between #{pageno}*10-9 and #{pageno}*10;

	select count(1)
	from tower_user_tx_record a,
	     tower_user_info b,
	     tower_area c,
	     tower_city d
	where 1 = 1
	  
	 
	  and c.City_id=#{admincity}
	  
	  
   and c.City_id in(
  
   #{city.cityid}
  
  )
  
	  
	  and c.ID in(
	  
	   #{area.id}
	  
	  )
	  
	 
	  and a.Wx_id = b.Wx_id 
	  and b.User_area = c.ID
	  and d.City_id = c.City_id
	  
	  and b.User_tele=#{parameter.usertele}
	  
	  
	  and b.User_name=#{parameter.username}
	  
	  
	  and b.User_area=#{parameter.userarea}
	  
	  
	  and b.user_level=#{parameter.userlevel}
	  
	  
	  and b.User_type=#{parameter.usertype}
	  
	  
	  and a.state=#{parameter.state}
	  
	  ;

mapper.update:
update tower_user_tx_record 
   set state = #{state}		       
 where Tx_id = #{txid};

update tower_user_charge 
   set Charge = Charge+#{fee},
       Get_now=get_now+#{fee},
       Getting_now=Getting_now-#{fee}
 where Wx_id in (select Wx_id from tower_user_tx_record where Tx_id= #{txid});

update tower_user_tx_source 
   set Tx_id = ''
 where Tx_id = (select ID from tower_user_tx_record where Tx_ID = #{txid});

update tower_user_charge 
   set Getting_now = Getting_now-#{fee},
       all_geting=all_geting+#{fee}
 where Wx_id in (select Wx_id from tower_user_tx_record where Tx_id= #{txid});

mapper.select:
select b.User_name as username, b.User_tele as usertele, c.area_name as areaname, d.City_name as cityname, (case b.user_level when 1 then '一级用户' when 2 then '二级用户' when 3 then '三级用户' when 4 then '四级用户' end ) as levelname, g.Type_name as typename, b.User_company as usercompany, a.Tx_id as txid, f.Charge as charge, f.Getting_now as gettingnow, f.Get_now as getnow, f.All_geting as allgeting, e.Bank_account as bankaccount, e.Bank_name as bankname, e.Bank_open as bankopen, e.Bank_cardid as bankcardid, a.Fee as fee, a.state as state, (case a.state when 0 then '提现申请中' when 1 then '审核通过，待打款' when 2 then '审核驳回' when 3 then '已打款' when 4 then '打款失败' when 5 then '用户已确认' when 6 then '自动确认' end ) as statename, a.Add_date as adddate, a.state as state, a.Bz as bz from tower_user_tx_record a, tower_user_info b left join tower_user_type g on b.User_type = g.ID, tower_area c, tower_city d, tower_user_bank e, tower_user_charge f where a.Wx_id = b.Wx_id and b.User_area = c.ID and d.City_id = c.City_id and a.Card_id = e.id and a.Wx_id = f.Wx_id
and c.City_id=#{admincity}
and c.City_id in(
#{city.cityid}
)
and c.ID in(
#{area.id}
)
and a.Wx_id = b.Wx_id and b.User_area = c.ID and d.City_id = c.City_id
and b.User_tele=#{parameter.usertele}
and b.User_name=#{parameter.username}
and b.User_area=#{parameter.userarea}
and b.user_level=#{parameter.userlevel}
and b.User_type=#{parameter.usertype}
and a.state=#{parameter.state}
order by a.Add_date desc
